@Component({
  selector: 'app-ex2',
  templateUrl: './ex2.component.html',
  styleUrls: ['./ex2.component.css']
})
export class Ex2Component {
  cor = 'cor';
  mudaCor(){
    this.cor;
  }
}
<div (mouseOver)="mudaCor()">Construa uma div que, quando o mouse passar por cima, mude de cor. Utilize bindings de via única.</div>
