@Component({
  selector: 'app-ex2',
  templateUrl: './ex2.component.html',
  styleUrls: ['./ex2.component.css']
})
export class Ex2Component {
  cor ='nada';

  mudaCor(){
    this.cor = 'cor';
  }
}
<div (mouseOver)="mudaCor()" [class]="cor">Construa uma div que, quando o mouse passar por cima, mude de cor. Utilize bindings de via única.</div>
