import { Component } from '@angular/core';

@Component({
  selector: 'app-ex3',
  templateUrl: './ex3.component.html',
  styleUrls: ['./ex3.component.css']
})
export class Ex3Component {
  estrela =0;
  valor = 1;
  avaliacao(){
    this.valor = this.estrela
  }
}
<p>0. Elabore um componente que mostre os valores de avaliação de filmes usando um select (notas 1, 2, 3, 4 e 5).
    Quando um valor for selecionado no select, devem ser exibidas estrelas referentes a nota concedida na parte
    inferior do componente</p>

    <div>
        <img src="https://jardimdasamericas.com.br/uploads/2023/04/capa-filme-super-mario-bros-filme-3cb50-large.jpg" alt="mario" width="300px" height="400px">
        <br>
        <div style="display: flex;">
            <label [(NgModel)]="estrela" for="">Avaliaçao</label>
            <select type="number" name="estrela"   (click)="avaliacao()">
                <option value="1">1</option>
                <option value="2">2</option>
                <option value="3">3</option>
                <option value="4">4</option>
                <option value="5">5</option>
            </select>
            <div>
                {{valor}}
            </div>
        </div>
    </div>
